import { createMultiStyleIconSet } from "@expo/vector-icons";
import React, { useState } from "react";
import { Card } from "react-native-elements"
import { View, Text, StyleSheet, TouchableOpacity } from "react-native";
import { AuthContext } from "../providers/AuthProvider";
import { Logout } from "../requests/LogoutRequest";

const TransactionScreen = ({ navigation }) => {

    return (
        <AuthContext.Consumer>
            {
                (auth) => (
                    <View style={styles.container}>
                        <Card style={styles.footer}>
                            <Card.Title><Text style={styles.text_footer}>Your Transactions</Text></Card.Title>
                        </Card>
                        <TouchableOpacity
                            onPress={async () => {
                                await Logout(auth.token).then((response) => {
                                    if (response.ok && response.data.status === "success") {
                                        console.log(response.data);
                                        auth.setIsLoggedIn(false);
                                        auth.setToken(null);
                                        auth.setCurrentAdmin(0);
                                    }
                                    else {
                                        alert("Token Manipulated!");
                                    }
                                });

                            }}
                            style={styles.button}
                        >
                            <LinearGradient colors={["#08D4C4", "#01AB9D"]}
                                style={styles.signIn}>
                                <Text style={styles.textSign}>Don't Have an Account? Sign Up!</Text>
                            </LinearGradient>
                        </TouchableOpacity>

                    </View>
                )
            }
        </AuthContext.Consumer>

    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#009387'
    },
    header: {
        flex: 1,
        justifyContent: 'flex-end',
        paddingHorizontal: 20,
        paddingBottom: 50
    },
    footer: {
        flex: 3,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30
    },
    text_header: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 30
    },
    text_footer: {
        color: '#05375a',
        fontSize: 18
    },
    action: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 5,
        paddingTop: 15
    },
    actionError: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#FF0000',
        paddingBottom: 5
    },
    textInput: {
        flex: 1,
        //marginTop: Platform.OS === 'ios' ? 0 : -12,
        paddingLeft: 10,
        color: '#05375a',
    },
    errorMsg: {
        color: '#FF0000',
        fontSize: 14,
    },
    button: {
        alignItems: 'center',
        marginTop: 50
    },
    signIn: {
        width: '100%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10
    },
    textSign: {
        fontSize: 18,
        fontWeight: 'bold',
        color: "white"
    }
});

export default TransactionScreen;
